Scene.Script:
-- Keep parsed colour for speed red = color("red") navs = {}
function events.onconstruct() local mns = game:maneuvers() local pct = -16 for _, maneuver in pairs(mns) do -- MNVR_dock.3db has different dimensions to the other buttons -- This can be removed in modded versions local mdltr = "0.0 0.0 70.0 77.0" local scissor = false if string.match(maneuver.activemodel, "MNVR_dock.3db") then mdltr = "0.0 -0.032 69.0 77.0" scissor = true end
local b = dom:addbutton({ anchor = "top", y = "0.1%", x = (pct .. '%'), height = "7.1%", ratio = 1.2, scissor = scissor, models = { { path = maneuver.inactivemodel, transform = "0.0 0.0 70.0 77.0" }, { path = maneuver.activemodel, transform = mdltr } }, onclick = "navclick('" .. maneuver.action .. "')" }) navs[maneuver.action] = b pct = pct + 10.5 end if game:multiplayer() then dom:element('nnbox5'):hide() else dom:element('nnbox7'):hide() end end function events.onnavchange(action) setnav(action) end function navclick(action) if game:setmaneuver(action) then setnav(action) end end function setnav(action) for k, button in pairs(navs) do if k == action then button:modelindex(1) else button:modelindex(0) end end end function events.onupdate() -- Number Box local n = dom:element('nums') local tleft = n:text('left') local pct = game:thrustpct() if pct < 20 then tleft:color(red) end tleft:value(pct .. '%') local speed = game:speed() if speed > 1000 then n:text('right'):value('---') else n:text('right'):value(game:speed()) end -- Other stuff below end
